CREATE TABLE WFCONFIG.CC_CARD_CATEGORY_WISE_CREDIT_LIMITS (
	CCL_ID INTEGER NOT NULL,
	MAIN_CATEGORY VARCHAR(25) NOT NULL,
	SUB_CATEGORY VARCHAR(25) NOT NULL,
	LOWER_LIMIT DECIMAL(25,2) DEFAULT 0.00 NOT NULL,
	UPPER_LIMIT DECIMAL(25,2) DEFAULT 0.00 NOT NULL,
	PRIMARY KEY (CCL_ID)
);
CREATE TABLE WFCONFIG.CC_CARD_CATEGORY_WISE_ELIGIBILITES (
	CCE_ID INTEGER NOT NULL,
	MAIN_CATEGORY VARCHAR(25) NOT NULL,
	SUB_CATEGORY VARCHAR(25) NOT NULL,
	SUB_SELECTION VARCHAR(25) NOT NULL,
	PRIMARY KEY (CCE_ID,MAIN_CATEGORY,SUB_CATEGORY,SUB_SELECTION)
);
CREATE TABLE WFCONFIG.DOCUMENT_TYPE_BASE (
	DID INTEGER NOT NULL,
	DOCUMENT_TYPE VARCHAR(100) NOT NULL,
	PRIMARY KEY (DID)
);
CREATE TABLE WFCONFIG.PRODUCT_BASE (
	PID INTEGER NOT NULL,
	PRODUCT_CODE VARCHAR(5) NOT NULL,
	WF_PRODUCT_CODE VARCHAR(5) NOT NULL,
	PRODUCT_NAME VARCHAR(100) NOT NULL,
	PRODUCT_DESCRIPTION VARCHAR(200),
	PRODUCT_CATEGORY_ID INTEGER,
	PRIMARY KEY (PID)
);
CREATE TABLE WFCONFIG.PRODUCT_CATEGORY_BASE (
	PRODUCT_CATEGORY_ID INTEGER NOT NULL,
	PRODUCT_CATEGORY VARCHAR(25) NOT NULL,
	PRIMARY KEY (PRODUCT_CATEGORY_ID)
);
CREATE TABLE WFCONFIG.PRODUCT_DOCUMENT_CHECKLIST_MAPPING (
	PDCID INTEGER NOT NULL,
	PRODUCT_ID INTEGER NOT NULL,
	DOCUMENT_ID INTEGER NOT NULL,
	SCAN_REQUIRED CHARACTER(1) DEFAULT 'Y' NOT NULL,
	MANDATORY CHARACTER(1) DEFAULT 'Y',
	PRIMARY KEY (PDCID)
);
ALTER TABLE WFCONFIG.PRODUCT_BASE
	ADD FOREIGN KEY (PRODUCT_CATEGORY_ID) 
	REFERENCES PRODUCT_CATEGORY_BASE (PRODUCT_CATEGORY_ID);


ALTER TABLE WFCONFIG.PRODUCT_DOCUMENT_CHECKLIST_MAPPING
	ADD FOREIGN KEY (PRODUCT_ID) 
	REFERENCES PRODUCT_BASE (PID);

ALTER TABLE WFCONFIG.PRODUCT_DOCUMENT_CHECKLIST_MAPPING
	ADD FOREIGN KEY (DOCUMENT_ID) 
	REFERENCES DOCUMENT_TYPE_BASE (DID);


